// he utilizado un nodo de inyectar mensaje para hacer las pruebas . El mensaje lo estructurado en un formato JSON


var Semanas = msg.payload.Semanas;
var Horas_por_semana = msg.payload.Horas_por_semana;
var Constante  = msg.payload.Constante;
var Dificultad = msg.payload.Dificultad;
var Interes = msg.payload.Interes;
var Nota_actividad = msg.payload.Nota_actividad;

// realizamos los calculos

var Horas_base =  Semanas * Horas_por_semana * Constante
var Horas_estudio = Horas_base*(1+Dificultad+Interes+Nota_actividad);

// pasamos las dos variales calculadas al siguiente nodo estructurandolo como un Objeto JAVASCRIPT

msg.payload = {"Horas_bases": Horas_base, "Horas_estudio": Horas_estudio };


return msg;

//recogemos el valor de la variable desde el msg.payload que es el mesnaje que se pasan entre los nodos

var Horas_estudio = msg.payload.Horas_estudio;

// realizamos los calculos redondeando para que las variables Dias de salida sean valores enteros 

var Dias_estudio = Math.round(Horas_estudio / 4 );
var Horas_estudio_dia = Math.round((Horas_estudio * 1) / (Dias_estudio + 1) );
var Horas_estudio_dia_anterior = Horas_estudio_dia * 2;

// construimos el mesnaje de salida para el siguiente nodo

msg.payload = {"Dias_estudio":Dias_estudio,"Horas_estudio_dia": Horas_estudio_dia, "Horas_estudio_dia_anterior" : Horas_estudio_dia_anterior} ;
